import jxl.*
def uname
def password
String filepath = "C:/Users/GunasekharKeserla/Desktop/Customer.xls"
File Excel = new File(filepath)
Workbook wb = Workbook.getWorkbook(Excel)
//This is the excel with contains workbook contains sheet
Sheet sh=wb.getSheet("Admin Login and Logout")

def rowsize = sh.getRows().toInteger()
def columnsize=sh.getColumns().toInteger()

log.info "The Total number of rows are "+rowsize
log.info "The Total number of columns are "+columnsize

for(def i=0; i<rowsize; i++)
{
	for(def j=0; j<columnsize; j++)
	{
		if(j==0){
			uname=sh.getCell(j,i).getContents()
			log.info uname
		}
		else{
				password=sh.getCell(j,i).getContents()
				log.info password
		}

	}

	def tc=testRunner.testCase.testSuite.project.testSuites['Rest Admin API - Property and Property Transfer'].testCases['Admin Login and Logout']
	//Set username and password in the property file which I am fetching them from excel

	tc.testSteps["Properties"].setPropertyValue("username",uname)
	tc.testSteps["Properties"].setPropertyValue("password",password)

	log.info "uname is $uname"
	log.info "password is $password"

	// To disable Admin Login Request during run-time to avoid confilct
testRunner.testCase.testSuite.project.testSuites['Rest Admin API - Property and Property Transfer'].testCases['Admin Login and Logout'].getTestStepByName('Admin Login Request').setDisabled(true)

	def runner = tc.run(null,false)
	log.info  runner.status
	log.info "Number of test Step got executed "+ runner.results.size()

	testRunner.testCase.testSuite.project.testSuites['Rest Admin API - Property and Property Transfer'].testCases['Admin Login and Logout'].getTestStepByName('Admin Login Request').setDisabled(false)

}

import jxl.*
def firstname
def lastname
def email
def password
def confirm
def telephone
String filepath = "C:/Users/GunasekharKeserla/Desktop/Customer.xls"
File Excel = new File(filepath)
Workbook wb = Workbook.getWorkbook(Excel)
//This is the excel with contains workbook contains sheet
Sheet sh=wb.getSheet("Add and Delete Customer")

def rowsize = sh.getRows().toInteger()
def columnsize=sh.getColumns().toInteger()

log.info "The Total number of rows are "+rowsize
log.info "The Total number of columns are "+columnsize

for(def i=0; i<rowsize; i++)
{
	for(def j=0; j<columnsize; j++)
	{
		if(j==0){
			//uname=sh.getCell(j,i).getContents()
			firstname=sh.getCell(j,i).getContents()
			log.info firstname
		}
		if(j==1){
			lastname=sh.getCell(j,i).getContents()
			log.info lastname
		}
		if(j==2){
			email=sh.getCell(j,i).getContents()
			log.info email
		}
		if(j==3){
			password=sh.getCell(j,i).getContents()
			log.info password
		}
		if(j==4){
			confirm=sh.getCell(j,i).getContents()
			log.info confirm
		}
		if(j==5){
			telephone=sh.getCell(j,i).getContents()
			log.info telephone
		}


		}


	def tc=testRunner.testCase.testSuite.project.testSuites['Rest Admin API - Property and Property Transfer'].testCases['Admin - Add Customer and Delete Customer']
	//Set username and password in the property file which I am fetching them from excel

	tc.testSteps["Properties"].setPropertyValue("firstname",firstname)
	tc.testSteps["Properties"].setPropertyValue("lastname",lastname)
	tc.testSteps["Properties"].setPropertyValue("email",email)
	tc.testSteps["Properties"].setPropertyValue("password",password)
	tc.testSteps["Properties"].setPropertyValue("confirm",confirm)
	tc.testSteps["Properties"].setPropertyValue("telephone",telephone)

	log.info "firstname is $firstname"
	log.info "lastname is $lastname"
	log.info "email is $email"
	log.info "password is $password"
	log.info "confirm is $confirm"
	log.info "telephone is $telephone"

	def runner = tc.run(null,false)
	log.info  runner.status
	log.info "Number of test Step got executed "+ runner.results.size()

